require_once 'vendor/autoload.php';\nuse PHPAuth\Auth;\nuse PHPAuth\Hash;\n\n$auth = new Auth($dbh, $config); // $dbh is a PDO instance, $config is an array of settings\n\n// Check if the user is logged in\nif (!$auth->isLogged()) {\n    // Redirect to the login page\n    header('Location: login.php');\n    exit;\n}\n\n// If the user is logged in, show the protected content\n// You can use $auth->getUser() to get the current user's details\n\n// To protect .pdf and .doc files, you can use a .htaccess file with a PHP handler\n// For example:\n// <FilesMatch "\.(pdf|doc)$">\n//     SetHandler php-handler\n// </FilesMatch>\n\n// Then, in your PHP script, you can check if the user is logged in before serving the file\nif ($auth->isLogged()) {\n    // Serve the file\n    header('Content-Type: application/pdf'); // or application/msword for .doc files\n    readfile('path/to/your/file.pdf');\n} else {\n    // Redirect to the login page\n    header('Location: login.php');\n    exit;\n}