$data = array(\n    array('Date' => '1/1/2009', 'Value' => 100),\n    array('Date' => '1/5/2009', 'Value' => 150),\n    array('Date' => '1/6/2009', 'Value' => 165),\n    array('Date' => '1/13/2009', 'Value' => 200),\n    array('Date' => '1/20/2009', 'Value' => 350),\n    array('Date' => '1/30/2009', 'Value' => 500)\n);\n\n$chartData = array();\nforeach ($data as $point) {\n    $date = DateTime::createFromFormat('m/d/Y', $point['Date']);\n    $chartData[] = array($date->format('Y,m,d'), $point['Value']);\n}\n\n$chartOptions = array(\n    'title' => 'Chart Title',\n    'hAxis' => array(\n        'title' => 'Date',\n        'format' => 'MMM d, y'\n    ),\n    'vAxis' => array(\n        'title' => 'Value'\n    )\n);\n\n$chartJson = json_encode(array(\n    'data' => $chartData,\n    'options' => $chartOptions\n));\n\necho '<script type="text/javascript" src="https://www.gstatic.com/charts/loader.js"></script>';\necho '<script type="text/javascript">';\necho 'google.charts.load("current", {"packages":["line"]});';\necho 'google.charts.setOnLoadCallback(drawChart);';\necho 'function drawChart() {';\necho 'var data = google.visualization.arrayToDataTable(' . $chartJson . ');';\necho 'var chart = new google.visualization.LineChart(document.getElementById("chart_div"));';\necho 'chart.draw(data, options);';\necho '}';\necho '</script>';\necho '<div id="chart_div"></div>';