// login.php\nif (array_key_exists('login', $_POST)) {\n    // ...\n    else {\n        $token = uniqid();\n        $_SESSION['error_token'] = $token;\n        header('Location: ' . $_SERVER['HTTP_REFERER'] . '?error_token=' . $token);\n        exit;\n    }\n}\n\n// details.php (or any other page)\nif (isset($_GET['error_token']) && $_GET['error_token'] === $_SESSION['error_token']) {\n    echo 'Login failed';\n    unset($_SESSION['error_token']);\n}