class Person {\n    private $id;\n    private $name;\n    private $email;\n\n    public function __construct($id, $name, $email) {\n        $this->id = $id;\n        $this->name = $name;\n        $this->email = $email;\n    }\n\n    // getters and setters...\n}\n\n$dsn = 'mysql:host=localhost;dbname=your_database';\n$username = 'your_username';\n$password = 'your_password';\n\n$pdo = new PDO($dsn, $username, $password);\n\n$stmt = $pdo->prepare('SELECT * FROM persons');\n$stmt->execute();\n\n$persons = array();\nwhile ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n    $person = new Person($row['id'], $row['name'], $row['email']);\n    $persons[] = $person;\n}\n\n// now you have an array of Person objects\nforeach ($persons as $person) {\n    echo $person->getName() . ' (' . $person->getEmail() . ')';\n}