require __DIR__ . '/vendor/autoload.php';\n\n$client = new Google_Client();\n$client->setApplicationName("Client_Gmail");\n$client->setAuthConfig('credentials.json');\n$client->setScopes(['https://mail.google.com/mail.readonly']);\n$client->setRedirectUri('YOUR_REDIRECT_URI');\n\n$gmail_service = new Google_Service_Gmail($client);\n\n// Get the inbox messages\n$messagesResponse = $gmail_service->users_messages->listUsersMessages('me', ['labelIds' => ['INBOX']]);\n$messages = $messagesResponse->getMessages();\n\n// Loop through each message and retrieve the email\nforeach ($messages as $message) {\n    $messageId = $message->getId();\n    $messageResponse = $gmail_service->users_messages->get('me', $messageId);\n    $email = $messageResponse->getPayload()->getBody()->getTextBody();\n    // Do something with the email\n}