$stmt = $db->prepare('\n    SELECT zipcode, ST_Distance(geography(POINT(:longitude, :latitude)), geography(POINT(longitude, latitude))) AS distance\n    FROM zipcodes\n    WHERE ST_DDistance(geography(POINT(:longitude, :latitude)), geography(POINT(longitude, latitude))) <= :radius\n    ORDER BY distance\n');\n$stmt->bindParam(':latitude', $coords['latitude']);\n$stmt->bindParam(':longitude', $coords['longitude']);\n$stmt->bindParam(':radius', $radius);\n$stmt->execute();