$role_drop = "<select name='role'>";\n$role_drop .= "\r\n<option value='Resident Assistant'>Resident Assistant</option>";\n$role_drop .= "\r\n<option value='Community Assistant'>Community Assistant</option>";\n$role_drop .= "\r\n<option value='Head RA'>Head RA</option>";\n$role_drop .= "\r\n</select>";\n\n$result = mysql_query("SELECT role FROM users");\nif (mysql_num_rows($result)) {\n    while ($r = mysql_fetch_assoc($result)) {\n        $role = $r["role"];\n        $temp_role_drop = $role_drop;\n        $temp_role_drop = str_replace(">$role</option>", "selected=\"\" >$role</option>", $temp_role_drop);\n        echo $temp_role_drop;\n    }\n}