function f_parse_csv($file, $longest, $delimiter) {\n    $mdarray = array();\n    $file    = fopen($file, "r");\n    while ($line = fgetcsv($file, $longest, $delimiter)) {\n        array_push($mdarray, $line);\n    }\n    fclose($file);\n    return $mdarray;\n}\n\nfunction sort_by_column(&$array, $column, $direction = 'asc') {\n    usort($array, function($a, $b) use ($column, $direction) {\n        if ($direction == 'asc') {\n            return strcmp($a[$column], $b[$column]);\n        } else {\n            return strcmp($b[$column], $a[$column]);\n        }\n    });\n}\n\n// Load the CSV data\n$data = f_parse_csv('yourfile.csv', 1000, ',');\n\n// Sort the data by the date column (assuming it's the 3rd column, adjust accordingly)\nsort_by_column($data, 2, 'desc'); // 'desc' for most recent date first\n\n// Print the sorted data\nprint_r($data);