$mysqli = new mysqli('your_host', 'your_user', 'your_password', 'your_database');\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$author = $_SESSION['username'];\n$subject = $_POST['subject'];\n$body = $_POST['body'];\n$branched = $_POST['branched'];\n$time = time();\n$branchedFrom = $_POST['parent'];\n$id = $_POST['parent'];\n$next = 0;\n$previous = 0;\n$branchedTo = 0;\n\n$stmt = $mysqli->prepare("INSERT INTO offtopic VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)");\n$stmt->bind_param("sssiiiiis", $author, $subject, $body, $time, $next, $previous, $branchedFrom, $branchedTo, $id);\n\nif (!$stmt->execute()) {\n    echo "Error: " . $stmt->error;\n} else {\n    echo "Insertion successful!";\n}\n\n$stmt->close();\n$mysqli->close();