<?php\n// Use prepared statements to prevent SQL injection\n$stmt = $mysqli->prepare("SELECT * FROM table ORDER BY col DESC");\n$stmt->execute();\n$result = $stmt->get_result();\n\n$html = "";\nwhile ($row = $result->fetch_assoc()) {\n    $html .= "<tbody><tr><td>&#8226;</td><td>" . htmlspecialchars($row["col"]) . "</td><td>" . htmlspecialchars($row["col"]) . "</td><td></td><td></td><tr><td></td><td colspan='4'>" . htmlspecialchars($row["col"]) . "</td></tr></tbody>";\n}\n\n// Return the HTML as a JSON object\nheader("Content-Type: application/json");\necho json_encode(array("html" => $html));