function getAdjacentTweets($key) {\n  $sql = "SELECT *\n          FROM tweets\n          WHERE tweet_key = '$key'\n          UNION ALL\n          SELECT *\n          FROM tweets\n          WHERE tweet_id = (\n            SELECT MAX(tweet_id)\n            FROM tweets\n            WHERE tweet_id < (SELECT tweet_id FROM tweets WHERE tweet_key = '$key')\n          )\n          UNION ALL\n          SELECT *\n          FROM tweets\n          WHERE tweet_id = (\n            SELECT MIN(tweet_id)\n            FROM tweets\n            WHERE tweet_id > (SELECT tweet_id FROM tweets WHERE tweet_key = '$key')\n          )";\n\n  $result = mysql_query($sql) or die("DB Error : ". mysql_error());\n  return mysql_fetch_all($result);\n}