require_once 'autoloader.php';\n\n$feed = new SimplePie();\n$feed->set_feed_url('http://example.com/rss');\n$feed->init();\n\nif ($feed->error()) {\n    echo 'Error parsing feed: ' . $feed->error();\n} else {\n    echo 'Feed title: ' . $feed->get_title();\n    echo 'Feed description: ' . $feed->get_description();\n\n    foreach ($feed->get_items() as $item) {\n        echo 'Item title: ' . $item->get_title();\n        echo 'Item description: ' . $item->get_description();\n    }\n}