// matcher.php\n$db = new PDO('mysql:host=localhost;dbname=matcher', 'username', 'password');\n\n// Insert current IP into database\n$stmt = $db->prepare('INSERT INTO ips (ip) VALUES (:ip)');\n$stmt->execute([':ip' => $_SERVER['REMOTE_ADDR']]);\n\n// Retrieve last 5 IPs from database\n$stmt = $db->query('SELECT ip FROM ips ORDER BY id DESC LIMIT 5');\n$ips = $stmt->fetchAll(PDO::FETCH_COLUMN);\n\n// Display IPs\necho implode('<br>', $ips);