$sname = Zend_Gdata_Calendar::AUTH_SERVICE_NAME; // predefined service name for calendar\n$client = Zend_Gdata_ClientLogin::getHttpClient($userName,$password,$sname);\n$service = new Zend_Gdata_Calendar($client);\n\n// Get the list of available calendars\n$calendars = $service->getCalendarListFeed();\n\n// Find the calendar you want to insert into\n$calendarId = null;\nforeach ($calendars as $calendar) {\n    if ($calendar->getTitle()->getText() == 'Your Calendar Name') {\n        $calendarId = $calendar->getId();\n        break;\n    }\n}\n\n// Create the event\n$event = $service->newEventEntry();\n$event->title = $service->newTitle($name);\n$event->when = array($when);\n\n// Insert the event into the non-default calendar\n$uri = "http://www.google.com/calendar/feeds/$calendarId/private/full";\n$newEvent = $service->insertEvent($event, $uri);