// Set the shared session ID for caching\nfunction set_cache_session_id() {\n    session_id('GlobalCache');\n}\n\n// Set the user's session ID\nfunction set_user_session_id() {\n    session_id();\n}\n\n// Get the cached value\nfunction get_cache($name) {\n    set_cache_session_id();\n    $value = $_SESSION[$name];\n    set_user_session_id();\n    return $value;\n}\n\n// Set the cached value\nfunction set_cache($name, $value) {\n    set_cache_session_id();\n    $_SESSION[$name] = $value;\n    set_user_session_id();\n}