// components/com_users/controllers/user.php (override)\n\npublic function login()\n{\n    // Get the login form data\n    $data = JFactory::getApplication()->input->post->get('jform', array(), 'array');\n\n    // Create an instance of the administrator login controller\n    $adminController = new JControllerAdministrator();\n    $adminController->login($data);\n\n    // Continue with the frontend login process\n    parent::login();\n}