<?php\nrequire 'vendor/autoload.php';\n\nuse Nexmo\Client;\n\n$client = new Client(new Nexmo\Client\Credentials\Basic('YOUR_API_KEY', 'YOUR_API_SECRET'));\n\n$incomingMessage = $client->get('/account/numbers/INCOMING_NUMBER/incoming-messages', [\n    'limit' => 1\n]);\n\n$message = $incomingMessage['messages'][0];\n\n// Store the message data in your database\n$db = new PDO('mysql:host=YOUR_DB_HOST;dbname=YOUR_DB_NAME', 'YOUR_DB_USERNAME', 'YOUR_DB_PASSWORD');\n$stmt = $db->prepare('INSERT INTO messages (from_number, message_text) VALUES (:from_number, :message_text)');\n$stmt->bindParam(':from_number', $message['from_number']);\n$stmt->bindParam(':message_text', $message['message_text']);\n$stmt->execute();