<?php\n\nfunction test_try_catch() {\n    try {\n        throw new Exception('Test exception');\n    } catch (Exception $e) {\n        // Do nothing\n    }\n}\n\nfunction test_if() {\n    if (true) {\n        // Do nothing\n    }\n}\n\n$start_time = microtime(true);\nfor ($i = 0; $i < 10000; $i++) {\n    test_try_catch();\n}\n$try_catch_time = microtime(true) - $start_time;\n\n$start_time = microtime(true);\nfor ($i = 0; $i < 10000; $i++) {\n    test_if();\n}\n$if_time = microtime(true) - $start_time;\n\necho "Try-catch time: $try_catch_time seconds\n";\necho "If time: $if_time seconds\n";\n\n?>