function reduceArray($array, $newSize) {\n    $oldSize = count($array);\n    $ratio = $oldSize / $newSize;\n    $newArray = array();\n\n    for ($i = 0; $i < $newSize; $i++) {\n        $sum = 0;\n        $count = 0;\n        for ($j = floor($i * $ratio); $j < floor(($i + 1) * $ratio); $j++) {\n            $sum += $array[$j];\n            $count++;\n        }\n        $newArray[] = $sum / $count;\n    }\n\n    return $newArray;\n}