$array = array(\n    'user_id' => 123,\n    'photo_url' => 'https://example.com/photo.jpg',\n    'name' => 'John Doe'\n);\n\n$memcache = new Memcache;\n$memcache->connect('localhost', 11211);\n\n$serialized_array = serialize($array);\n$memcache->set('user_data', $serialized_array);\n\n// To retrieve the array\n$retrieved_array = $memcache->get('user_data');\n$unserialized_array = unserialize($retrieved_array);\n\nprint_r($unserialized_array);