function pronounceabilityScore($word) {\n    $score = 0;\n    $vowels = 'aeiouy';\n    $consonants = 'bcdfghjklmnpqrstvwxyz';\n\n    // Vowel frequency\n    $vowelCount = 0;\n    for ($i = 0; $i < strlen($word); $i++) {\n        if (strpos($vowels, strtolower($word[$i])) !== false) {\n            $vowelCount++;\n        }\n    }\n    $score += $vowelCount / strlen($word);\n\n    // Consonant frequency\n    $consonantCount = 0;\n    for ($i = 0; $i < strlen($word); $i++) {\n        if (strpos($consonants, strtolower($word[$i])) !== false) {\n            $consonantCount++;\n        }\n    }\n    $score += $consonantCount / strlen($word);\n\n    // Consecutive vowel or consonant count\n    $consecutiveCount = 0;\n    $prevType = null;\n    for ($i = 0; $i < strlen($word); $i++) {\n        $isVowel = strpos($vowels, strtolower($word[$i])) !== false;\n        if ($prevType !== null && $isVowel === $prevType) {\n            $consecutiveCount++;\n        }\n        $prevType = $isVowel;\n    }\n    $score -= $consecutiveCount / strlen($word);\n\n    // Length\n    $score += 1 / (1 + pow(strlen($word), 1.5));\n\n    return $score;\n}\n\nprint(pronounceabilityScore('nameoic') . "\n");\nprint(pronounceabilityScore('namelet') . "\n");