// Load the PowerPoint file\nuse PhpPptx\PhpPptx;\n$pptx = new PhpPptx('input.pptx');\n\n// Get the slides and loop through them\n$slides = $pptx->getSlides();\nforeach ($slides as $slide) {\n    // Get the bullet points from the slide\n    $bulletPoints = $slide->getBulletPoints();\n    foreach ($bulletPoints as $bulletPoint) {\n        // Extract the text from the bullet point\n        $text = $bulletPoint->getText();\n        \n        // Write the text to the Excel file\n        use PhpExcel\PhpExcel;\n        $excel = new PhpExcel();\n        $sheet = $excel->getActiveSheet();\n        $sheet->setCellValue('A' . $row, $text);\n        $sheet->setCellValue('B' . $row, $slide->getPageNumber());\n        $row++;\n    }\n}\n\n// Save the Excel file\n$excel->save('output.xlsx');