use Slim\App;\nuse Slim\Http\Request;\nuse Slim\Http\Response;\n\n$app = new App();\n\n$app->post('/transaction/update', function (Request $request, Response $response) {\n    $data = $request->getParsedBody();\n    $uniqueReference = $data['unique_reference'];\n    $amount = $data['amount'];\n    $status = $data['status'];\n    $statusDescription = $data['status_description'];\n\n    // Validate and sanitize input\n    if (!ctype_alnum($uniqueReference) || !is_numeric($amount) || !in_array($status, ['success', 'failed'])) {\n        return $response->withStatus(400)->withJson(['error' => 'Invalid input']);\n    }\n\n    // Update transaction status in database\n    // ...\n\n    return $response->withStatus(200)->withJson(['message' => 'Transaction updated successfully']);\n});