// Single database query\n$posts = $db->query("SELECT * FROM posts WHERE post_date >= NOW() - INTERVAL 1 YEAR");\n\n// Filter the results in PHP\n$yearlyPosts = $posts;\n$monthlyPosts = array_filter($yearlyPosts, function($post) {\n    return $post['post_date'] >= date('Y-m-d', strtotime('-1 month'));\n});\n$weeklyPosts = array_filter($monthlyPosts, function($post) {\n    return $post['post_date'] >= date('Y-m-d', strtotime('-1 week'));\n});