function reportThread() {\n    global $id;\n\n    $db = new PDO('mysql:host=your_host;dbname=your_database', 'your_username', 'your_password');\n\n    $stmt = $db->prepare('SELECT 1 FROM reports WHERE url = :url AND is_checked = 0');\n    $stmt->bindParam(':url', $id);\n    $stmt->execute();\n\n    if ($stmt->fetch() === false) {\n        // No existing report with is_checked = 0, insert a new report\n        $stmt = $db->prepare('INSERT INTO reports (url, userID) VALUES (:url, 1)');\n        $stmt->bindParam(':url', $id);\n        $stmt->execute();\n\n        echo "<script type=\"text/javascript\">alert(\"A moderator has been notified.\");</script>";\n    } else {\n        echo "<script type=\"text/javascript\">alert(\"There's already a pending report on this thread.\");</script>";\n    }\n}