function getRowsByProductSearch($searchString, $table, $max) {\n    $searchString = mysql_real_escape_string($searchString);\n    $result = mysql_query("SELECT Product_NO, USERNAME, ACCESSSTARTS, Product_NAME, mycolumn, date_format(mycolumn, '%d %m %Y') as shortDate FROM {$table} WHERE Product_NAME LIKE '%" . $searchString . "%'" . $max);\n    if($result === false) {\n        echo mysql_error();\n        return array();\n    }\n    $rows = array();\n    while($row = mysql_fetch_assoc($result)) {\n        echo "test while <p>";\n        $rows[] = $row;\n    }\n    mysql_free_result($result);\n    return $rows;\n}