<?php\n\n// Configuration\n$host = 'your_email_host';\n$username = 'your_email_username';\n$password = 'your_email_password';\n$interval = 5; // minutes\n\n// Connect to POP email account\n$mailbox = imap_open("{{$host}/pop3}INBOX", $username, $password);\n\n// Check for new emails every $interval minutes\nwhile (true) {\n    // Get new emails\n    $emails = imap_search($mailbox, 'UNSEEN');\n\n    // Loop through new emails\n    foreach ($emails as $email_number) {\n        // Get email headers and body\n        $headers = imap_headerinfo($mailbox, $email_number);\n        $body = imap_body($mailbox, $email_number);\n\n        // Extract subject and content\n        $subject = $headers->subject;\n        $content = $body;\n\n        // Get attachments\n        $attachments = array();\n        foreach (imap_mime_headers($mailbox, $email_number) as $mime_header) {\n            if ($mime_header->subtype == 'IMAGE') {\n                $attachments[] = imap_fetchbody($mailbox, $email_number, $mime_header->number);\n            }\n        }\n\n        // Save information to file\n        $file = fopen('email_data.txt', 'a');\n        fwrite($file, "Subject: $subject\n");\n        fwrite($file, "Content: $content\n");\n        foreach ($attachments as $attachment) {\n            fwrite($file, "Attachment: " . base64_encode($attachment) . "\n");\n        }\n        fclose($file);\n    }\n\n    // Wait for $interval minutes\n    sleep($interval * 60);\n}\n\n// Close POP email account connection\nimap_close($mailbox);\n\n?>