class SqlColumn {\n    private $type;\n    private $dataType;\n    private $length;\n    private $collate;\n    private $charset;\n    private $nullify;\n    private $default;\n    private $increment;\n    private $unique;\n    private $primary;\n    private $comment;\n\n    public function __construct(array $config) {\n        // Initialize properties from the config array\n        $this->type = $config['type'];\n        $this->dataType = $config['data-type']['type'];\n        $this->length = $config['data-type']['length'];\n        $this->collate = $config['data-type']['collate'];\n        $this->charset = $config['data-type']['character-set'];\n        $this->nullify = $config['nullify'];\n        $this->default = $config['default'];\n        $this->increment = $config['increment'];\n        $this->unique = $config['unique'];\n        $this->primary = $config['primary'];\n        $this->comment = $config['comment'];\n    }\n\n    public function generateSql(): string {\n        // Start with the column type and length\n        $sql = "{$this->dataType}({$this->length})";\n\n        // Add collate and charset if specified\n        if ($this->collate && $this->charset) {\n            $sql .= " COLLATE {$this->collate} CHARACTER SET {$this->charset}";\n        }\n\n        // Handle nullability\n        $sql .= $this->nullify ? " NULL" : " NOT NULL";\n\n        // Add default value if specified\n        if ($this->default !== null) {\n            $sql .= " DEFAULT '{$this->default}'";\n        }\n\n        // Increment\n        if ($this->increment) {\n            $sql .= " AUTO_INCREMENT";\n        }\n\n        // Unique and primary keys\n        if ($this->unique) {\n            $sql .= " UNIQUE";\n        }\n        if ($this->primary) {\n            $sql .= " PRIMARY KEY";\n        }\n\n        // Comment\n        if ($this->comment) {\n            $sql .= " COMMENT '{$this->comment}'";\n        }\n\n        return $sql;\n    }\n}\n\n// Example usage\n$columnConfig = [\n    'type' => 'character',\n    'data-type' => [\n        'type' => 'varchar',\n        'length' => 255,\n        'collate' => 'utf8_unicode_ci',\n        'character-set' => 'utf8',\n    ],\n    'nullify' => true,\n    'default' => null,\n    'increment' => false,\n    'unique' => false,\n    'primary' => false,\n    'comment' => 'This is a small general text field.',\n];\n\n$column = new SqlColumn($columnConfig);\necho $column->generateSql();