$threadID = 1; // example thread ID\n\n$query = "\n  SELECT \n    p.id, \n    p.content, \n    fs.signature, \n    fs.forumRank, \n    fs.numPosts\n  FROM \n    Posts p\n  JOIN \n    ForumSettings fs ON p.userID = fs.userID\n  WHERE \n    p.threadID = :threadID\n";\n\n$stmt = $pdo->prepare($query);\n$stmt->bindParam(':threadID', $threadID);\n$stmt->execute();\n\n$posts = $stmt->fetchAll();\n\nforeach ($posts as $post) {\n  echo $post['content'] . '<br>';\n  echo 'Signature: ' . $post['signature'] . '<br>';\n  echo 'Forum Rank: ' . $post['forumRank'] . '<br>';\n  echo 'Number of Posts: ' . $post['numPosts'] . '<br><br>';\n}