use TA_Lib\TA;\n\n// Load the TA-Lib library\nrequire_once 'TA_Lib.php';\n\n// Define your stock data\n$stockData = array(\n    array('date' => '2022-01-01', 'close' => 100.0),\n    array('date' => '2022-01-02', 'close' => 105.0),\n    array('date' => '2022-01-03', 'close' => 110.0),\n    // ...\n);\n\n// Create a TA object\n$ta = new TA();\n\n// Set the input data\n$ta->SetInputData($stockData);\n\n// Calculate the 50-period SMA\n$sma = $ta->SMA(50);\n\n// Print the SMA values\nforeach ($sma as $value) {\n    echo $value . "\n";\n}