$username = 'username';\n$password = 'password';\n\n// Create a salt\n$salt = substr(str_shuffle('abcdefghijklmnopqrstuvwxyz0123456789'), 0, 2);\n\n// Encrypt the password using crypt()\n$encrypted_password = crypt($password, $salt);\n\n// Create the htpasswd line\n$htpasswd_line = "$username:$encrypted_password";\n\n// Write the line to the htpasswd file\n$file = fopen('htpasswd', 'a');\nfwrite($file, $htpasswd_line . "\n");\nfclose($file);