$closestPeople = array();\n\n// assuming you have the comparison results in an array\nforeach ($comparisonResults as $personId => $numSameAnswers) {\n    if (count($closestPeople) < 10) {\n        $closestPeople[$personId] = $numSameAnswers;\n    } else {\n        $lowest = min($closestPeople);\n        if ($numSameAnswers > $lowest) {\n            $key = array_search($lowest, $closestPeople);\n            unset($closestPeople[$key]);\n            $closestPeople[$personId] = $numSameAnswers;\n        }\n    }\n}