$dir = './';\n$dhandle = opendir($dir);\n$old_files = array();\n\nif ($dhandle) {\n    while (false !== ($fname = readdir($dhandle))) {\n        if (($fname != '.') && ($fname != '..')) {\n            $old_files[] = (is_dir("./$fname")) ? "(Dir) {$fname}" : $fname;\n        }\n    }\n    closedir($dhandle);\n}\n\nforeach ($old_files as $file) {\n    $new_file = str_replace(' ', '_', $file);\n    rename($file, $new_file);\n}