$dir_array = array(); // Initialize an empty array to hold the directories\n\n// Open the directory\nif ($handle = opendir('.')) {\n    while (false !== ($entry = readdir($handle))) {\n        // Check if the entry is a directory and not "." or ".."\n        if (is_dir($entry) && $entry !== '.' && $entry !== '..') {\n            $dir_array[] = $entry; // Add the directory to the array\n        }\n    }\n    closedir($handle);\n}\n\n// Now, $dir_array contains only the directories without "." and ".."