function is_business_hours($timestamp = null) {\n    if ($timestamp === null) {\n        $timestamp = time();\n    }\n    // your logic here\n}\n\n// Test the function with different times\n$test_times = array(\n    strtotime('Monday 08:00'), // before business hours\n    strtotime('Monday 10:00'), // during business hours\n    strtotime('Monday 18:00'), // after business hours\n    strtotime('Tuesday 02:00'), // after midnight, before business hours\n);\n\nforeach ($test_times as $test_time) {\n    echo date('Y-m-d H:i', $test_time) . ': ' . (is_business_hours($test_time) ? 'Open' : 'Closed') . "\n";\n}