// Example PDO connection\n$dsn = 'mysql:host=localhost;dbname=mydatabase';\n$username = 'myusername';\n$password = 'mypassword';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n    // Set PDO to throw exceptions on errors\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n\n// Example CRUD operations using PDO\n// Create\n$stmt = $pdo->prepare('INSERT INTO mytable (name, email) VALUES (:name, :email)');\n$stmt->execute(['name' => 'John Doe', 'email' => 'johndoe@example.com']);\n\n// Read\n$stmt = $pdo->query('SELECT * FROM mytable');\n$rows = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n// Update\n$stmt = $pdo->prepare('UPDATE mytable SET name = :name WHERE id = :id');\n$stmt->execute(['name' => 'Jane Doe', 'id' => 1]);\n\n// Delete\n$stmt = $pdo->prepare('DELETE FROM mytable WHERE id = :id');\n$stmt->execute(['id' => 1]);