<?php\n// languages/en.php\nreturn [\n    'Hello World' => 'Hello World',\n    // more translations here\n];\n\n// languages/nl.php\nreturn [\n    'Hello World' => 'Hallo Wereld',\n    // more translations here\n];\n\n// Your PHP code\nfunction _($string) {\n    $lang = 'en'; // set the current language\n    $translations = include 'languages/' . $lang . '.php';\n    return $translations[$string] ?? $string;\n}\n\necho _('Hello World');\n?>