function gmtOffsetToTimezone($offset) {\n    $offset = floatval($offset);\n    $hours = intval($offset);\n    $minutes = intval(($offset - $hours) * 60);\n\n    $tz = sprintf('Etc/GMT%+d:%02d', -$hours, abs($minutes));\n\n    return $tz;\n}\n\n$gmtOffset = '-8.0';\n$timezone = gmtOffsetToTimezone($gmtOffset);\n\ndate_default_timezone_set($timezone);\n// or\n$date = new DateTime();\n$date->setTimezone(new DateTimeZone($timezone));