$mysqli = new mysqli('your_host', 'your_username', 'your_password', 'your_database');\n\n$awards_sql_1 = $mysqli->query('SELECT * FROM categories WHERE section_id = 1');\n$awards_sql_2 = $mysqli->query('SELECT * FROM categories WHERE section_id = 2');\n$awards_sql_3 = $mysqli->query('SELECT * FROM categories WHERE section_id = 3');\n$awards_sql_4 = $mysqli->query('SELECT * FROM categories WHERE section_id = 4');\n\n$loop = 1;\n\nwhile($row_sections = $sections_query->fetch_assoc()) {\n    $category = 1;\n    echo "<h3>" . $row_sections['section_name'] . " (Loop# $loop)</h3>";\n\n    while($categories = ${"awards_sql_{$loop}"}->fetch_assoc()) {\n        $winners_sql = $mysqli->query("SELECT * FROM 2009_RKR_bestof WHERE section = $loop && category = $category ORDER BY result_level ASC");\n        echo "<h4><strong>{$categories['category_name']}</strong></h4>";\n        echo "<ul class=\"winners\">";\n\n        while($winners = $winners_sql->fetch_assoc()) {\n            switch ($winners['result_level']) {\n                case 1: $result_level = "Platinum"; break;\n                case 2: $result_level = "Gold"; break;\n                case 3: $result_level = "Silver"; break;\n            }\n            if (isset($winners['url'])) { $anchor = "<a href=\"http://{$winners['url']}\" target=\"_blank\">"; $close = "</a>"; }\n            echo "<li>$anchor{$winners['winner']}$close ($result_level)</li>";\n\n            unset($anchor);\n            unset($close);\n        }\n        echo "</ul>";\n        $category++;\n    }\n    $loop++;\n}