$classNames = get_declared_classes();\nforeach ($classNames as $className) {\n    try {\n        $reflectionClass = new ReflectionClass($className);\n        $isAbstract = $reflectionClass->isAbstract();\n\n        if ($isAbstract) {\n            echo "$className is an abstract class\n";\n        } else {\n            echo "$className is not an abstract class\n";\n        }\n    } catch (ReflectionException $e) {\n        echo "Error: $className does not exist\n";\n    }\n}