function updateDemo($demoTitle, $desc, $keyword, $uploadedFile, $clientname, $uploadedImage, $adminName, $demoID) {\n    $mysqli = new mysqli('your_host', 'your_user', 'your_pass', 'your_db');\n\n    $query = "UPDATE demos SET dmTitle = ?, dmDesc = ?, dmKey = ?, dmLink = ?, client = ?, imageTitle = ?, userName = ? WHERE id = ?";\n\n    $stmt = $mysqli->prepare($query);\n    $stmt->bind_param("sssssssi", $demoTitle, $desc, $keyword, $uploadedFile, $clientname, $uploadedImage, $adminName, $demoID);\n\n    if ($stmt->execute()) {\n        return 'yes';\n    } else {\n        return $mysqli->error;\n    }\n}