class TestClass {\n    private $varA;\n    private $varB;\n\n    public function __set($name, $value) {\n        if ($name === 'varA') {\n            $this->varA = $value;\n        } else {\n            throw new Exception("Cannot set property $name");\n        }\n    }\n\n    public function __get($name) {\n        if ($name === 'varB') {\n            return $this->varB;\n        } else {\n            throw new Exception("Cannot get property $name");\n        }\n    }\n}\n\n$x = new TestClass();\n$x->varA = "test";  // This is allowed\necho $x->varB;  // This is allowed\n\ntry {\n    $x->varC = "test";  // This will throw an exception\n} catch (Exception $e) {\n    echo $e->getMessage();\n}