$app = new \Slim\App;\n\n$app->get('/get_data', function ($request, $response) {\n    // Retrieve data from MySQL database\n    $data = array();\n    $sql = "SELECT * FROM your_table";\n    $result = $mysqli->query($sql);\n    while ($row = $result->fetch_assoc()) {\n        $data[] = $row;\n    }\n    $response->withJson($data);\n});\n\n$app->post('/add_data', function ($request, $response) {\n    // Add data to MySQL database\n    $data = $request->getParsedBody();\n    $sql = "INSERT INTO your_table (column1, column2) VALUES (?, ?)";\n    $stmt = $mysqli->prepare($sql);\n    $stmt->bind_param("ss", $data['column1'], $data['column2']);\n    $stmt->execute();\n    $response->withStatus(201);\n});\n\n$app->delete('/remove_data', function ($request, $response) {\n    // Remove data from MySQL database\n    $id = $request->getAttribute('id');\n    $sql = "DELETE FROM your_table WHERE id = ?";\n    $stmt = $mysqli->prepare($sql);\n    $stmt->bind_param("i", $id);\n    $stmt->execute();\n    $response->withStatus(204);\n});\n\n$app->run();