function create_hash($sender_id, $recipient_id) {\n  $hash = dechex($sender_id ^ $recipient_id);\n  return $hash;\n}\n\nfunction decode_hash($hash) {\n  $decoded = explode("^", hexdec($hash));\n  return array($decoded[0], $decoded[1]);\n}\n\n$sender_id = 123;\n$recipient_id = 456;\n$hash = create_hash($sender_id, $recipient_id);\necho "Hash: $hash\n";\n\n$decoded = decode_hash($hash);\necho "Decoded: Sender ID = $decoded[0], Recipient ID = $decoded[1]\n";