function cleanUrlString($str) {\n    $str = trim($str);\n    $str = preg_replace('/[^a-z0-9\s-]/i', '', $str); // remove non-alphanumeric, non-space, non-dash\n    $str = preg_replace('/\s+/', '-', $str); // replace one or more spaces with a dash\n    $str = preg_replace('/-+/', '-', $str); // replace multiple dashes with a single dash\n    $str = strtolower($str);\n    return $str;\n}