function timeout($func, $timeout) {\n    $start = microtime(true);\n    try {\n        $result = $func();\n    } catch (Exception $e) {\n        throw $e;\n    }\n    $end = microtime(true);\n    if ($end - $start > $timeout) {\n        throw new Exception('Timeout exceeded');\n    }\n    return $result;\n}\n\nfunction doSomething() {\n    // .. code here ..\n\n    $result = timeout('function1', 10); // execute function1 with 10 second timeout\n    // .. some code here ..\n}