<?php\n$db = new SQLite3('hits.db');\n$db->exec('CREATE TABLE IF NOT EXISTS hits (PageName TEXT PRIMARY KEY, Hits INTEGER)');\n\n$pageName = $_SERVER['REQUEST_URI'];\n$result = $db->query("SELECT Hits FROM hits WHERE PageName = '$pageName'");\n\nif ($result->fetchArray() === false) {\n    $db->exec("INSERT INTO hits (PageName, Hits) VALUES ('$pageName', 1)");\n} else {\n    $db->exec("UPDATE hits SET Hits = Hits + 1 WHERE PageName = '$pageName'");\n}\n\n$db->close();\n?>