function modifyXml($searchString, $data) {\n    $xmlFile = 'yourfile.xml';\n    if (file_exists($xmlFile)) {\n        $xml = simplexml_load_file($xmlFile);\n    } else {\n        $xml = new SimpleXMLElement('<markers></markers>');\n    }\n\n    // Remove existing elements with the same type\n    foreach ($xml->marker as $marker) {\n        if ((string)$marker->type == $searchString) {\n            $dom = dom_import_simplexml($marker);\n            $dom->parentNode->removeChild($dom);\n        }\n    }\n\n    // Add new data\n    if (!empty($data)) {\n        $marker = $xml->addChild('marker');\n        $marker->addChild('type', $searchString);\n        $marker->addChild('title', $data['title']);\n        $marker->addChild('address', $data['address']);\n        $marker->addChild('latitude', $data['latitude']);\n        $marker->addChild('longitude', $data['longitude']);\n    }\n\n    // Save the XML file\n    $xml->asXML($xmlFile);\n}\n\n// Example usage:\n$searchString = 'hotels';\n$data = array(\n    'title' => 'Hotel Title',\n    'address' => 'Hotel Address',\n    'latitude' => 'Hotel Latitude',\n    'longitude' => 'Hotel Longitude'\n);\n\nmodifyXml($searchString, $data);