function check_mod_rewrite() {\n    $url = 'http://' . $_SERVER['HTTP_HOST'] . $_SERVER['REQUEST_URI'];\n    $rewritten_url = $url . '?rewritten=1';\n\n    $headers = get_headers($rewritten_url);\n    if (!empty($headers[0]) && strpos($headers[0], '404') === false) {\n        // mod_rewrite is enabled\n        return true;\n    }\n\n    // mod_rewrite is disabled\n    return false;\n}\n\n// Example usage:\nif (check_mod_rewrite()) {\n    print "mod_rewrite is enabled";\n} else {\n    print "mod_rewrite is disabled";\n}