function checkDomainAvailability($domain) {\n    $status = 'available';\n    try {\n        $records = dns_get_record($domain, DNS_A);\n        if (!empty($records)) {\n            $status = 'unavailable';\n        }\n    } catch (Exception $e) {\n        // If dns_get_record fails, the domain is likely available\n    }\n    return $status;\n}\n\n$domain = 'example.com';\n$status = checkDomainAvailability($domain);\necho "Domain status: $status";